DROP TABLE IF EXISTS `residence`;
CREATE TABLE `residence` (
  `id` int(11) NOT NULL default '0',
  `name` varchar(40) NOT NULL default '',
  `lease` int(10) NOT NULL default '0',
  `desc` text NOT NULL,
  `location` varchar(15) NOT NULL default '',
  `paidUntil` decimal(20,0) NOT NULL default '0',
  `Grade` decimal(1,0) NOT NULL default '0',
  `price` int(11) NOT NULL default '0',
  `last_price` bigint(20) NOT NULL default '0',
  `deposit` int(10) NOT NULL default '0',
  `siegetype` varchar(15) NOT NULL default 'Auction',
  `lastSiegeDate` int(11) NOT NULL default '0',
  `siegeDate` int(11) NOT NULL default '0',
  `lastTax` int(11) NOT NULL default '0',
  `supplyLevel` int(11) NOT NULL default '0',
  `rewardLevel` int(11) NOT NULL default '0',
  `taxPercent` tinyint(3) unsigned NOT NULL default '15',
  `treasury` decimal(20,0) NOT NULL default '0',
  `changeTimeEnd` int(10) unsigned NOT NULL default '0',
  `changeTimeOver` enum('true','false') NOT NULL default 'true',
  `townId` int(11) NOT NULL default '0',
  `residenceType` varchar(15) NOT NULL default 'ClanHall',
  `castleId` int(11) NOT NULL default '0',
  PRIMARY KEY  (`id`,`name`),
  KEY `id` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

