DROP TABLE IF EXISTS `weapon`;
CREATE TABLE `weapon` (
  `item_id` smallint(4) NOT NULL DEFAULT '0',
  `name` varchar(128) NOT NULL DEFAULT '',
  `additional_name` varchar(128) NOT NULL DEFAULT '',
  `type2` varchar(50) NOT NULL DEFAULT 'weapon',
  `bodypart` varchar(16) NOT NULL DEFAULT '',
  `weight` int(11) NOT NULL DEFAULT '0',
  `soulshots` int(11) NOT NULL DEFAULT '0',
  `spiritshots` int(11) NOT NULL DEFAULT '0',
  `crystal_type` enum('none','d','c','b','a','s','s80','s84') DEFAULT NULL,
  `p_dam` int(11) NOT NULL DEFAULT '0',
  `rnd_dam` int(11) NOT NULL DEFAULT '0',
  `weaponType` varchar(12) NOT NULL DEFAULT '',
  `critical` double(11,2) NOT NULL DEFAULT '0.00',
  `hit_modify` double(11,2) DEFAULT '0.00',
  `avoid_modify` double(11,2) DEFAULT '0.00',
  `shield_def` int(11) NOT NULL DEFAULT '0',
  `shield_def_rate` int(11) NOT NULL DEFAULT '0',
  `attack_speed` int(11) NOT NULL DEFAULT '0',
  `attack_range` int(11) NOT NULL DEFAULT '40',
  `mp_consume` int(11) NOT NULL DEFAULT '0',
  `m_dam` int(11) NOT NULL DEFAULT '0',
  `durability` int(11) NOT NULL DEFAULT '-1',
  `period` int(11) NOT NULL DEFAULT '0',
  `price` int(11) NOT NULL DEFAULT '0',
  `crystal_count` int(11) NOT NULL DEFAULT '0',
  `icon` varchar(128) NOT NULL DEFAULT '',
  `dropable` tinyint(1) NOT NULL DEFAULT '1',
  `destroyable` tinyint(1) NOT NULL DEFAULT '1',
  `tradeable` tinyint(1) NOT NULL DEFAULT '1',
  `enchantable` tinyint(1) NOT NULL DEFAULT '0',
  `elementable` tinyint(1) NOT NULL DEFAULT '0',
  `keep_type` tinyint(4) NOT NULL DEFAULT '0',
  `skills` varchar(70) NOT NULL DEFAULT '0-0;',
  `enchant4_skills` varchar(70) NOT NULL DEFAULT '0-0;',
  `for_npc` tinyint(1) NOT NULL DEFAULT '0',
  `enchanted` smallint(4) NOT NULL DEFAULT '0',
  `olympiad_use` smallint(6) NOT NULL DEFAULT '1',
  `reuse_delay` int(11) NOT NULL DEFAULT '0',
  `delay_share_group` smallint(6) NOT NULL DEFAULT '-1',
  `is_magic_weapon` tinyint(1) NOT NULL DEFAULT '0',
  `is_masterwork` tinyint(1) NOT NULL DEFAULT '0',
  `is_have_sa` tinyint(4) NOT NULL DEFAULT '0',
  `is_pvp` tinyint(1) NOT NULL DEFAULT '0',
  `is_common` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`item_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

